      ),
      'vocabulary_uri' => 
      array (
        'hover_text' => 'The URI where this vocabulary is defined.<br> <a target=\'_blank\' href=\'https://iatistandard.org/en/iati-standard/203/activity-standard/iati-activities/iati-activity/tag/\'>For more information</a>',
        'help_text' => 'Provide a link to your chosen list.',
